mapper.select:
select ist.taskID, ist.assetID, ist.roomID, ist.machineID, ist.checkTime, ist.checkResult, ist.shouldRoomID, ist.resultCheck, org.orgName, rm.roomName FROM invetoryAsset ist LEFT JOIN asset ast ON ist.assetID = ast.ID LEFT JOIN room rm ON ist.roomID = rm.ID LEFT JOIN organize org ON ast.orgID = org.orgID
AND ist.taskID = #{invetoryassetQuery.taskID}
AND ist.checkResult = #{invetoryassetQuery.checkResult}
AND ist.resultCheck = #{invetoryassetQuery.resultCheck}
